import React from "react";
import axios from "axios";

function Payment(){
  const checkoutHandler = (amount) => {
    const {data: { key },} = axios.get("http://www.localhost:4000/api/getkey");

    const {data: { order },} =axios.post("http://localhost:4000/api/checkout", {
      amount,
    });
    // console.log(data)

    var options = {
      key:'rzp_test_uJifFy0kaDdhAs',
      amount: Number(order.amount),
      currency: "INR",
      name: "6 pack programmer",
      description: "tutorial Test Transaction",
      image: "https://www.gravatar.com/avatar/205e460b479e2e5b48aec07710c08d50",
      order_id: order.id, //This is a sample Order ID. Pass the `id` obtained in the response of Step 1
      callback_url: "https://localhost:4000/api/paymentverification",
      prefill: {
        name: "Gaurav Kumar",
        email: "gaurav.kumar@example.com",
        contact: "9000090000",
      },
      notes: {
        address: "Razorpay Corporate Office",
      },
      theme: {
        color: "#3399cc",
      },
    };
    const razor = new window.Razorpay(options);
    razor.open();
  };

  return (
    <>
        <div>
          <label for="exampleInputEmail1">Enter name</label>
          <input
            type="text"
            id="exampleInputEmail1"
            aria-describedby="emailHelp"
          />
        </div>
        <div>
          <label for="exampleInputPassword1">Amount</label>
          <input
            type="number"
            id="exampleInputPassword1"
          />
        </div>
        <button
          onClick={() => checkoutHandler(5000)}
        >
          pay now
        </button>
    </>
  );
}

